/* * This example creates a header style and then * adds it to the olap control's ColumnHeaderStyles collection */ private void Page_Load(object sender, System.EventArgs e) { if(!Page.IsPostBack) { MSSQLBI.Web.Olap.HeaderStyle colStyle = new MSSQLBI.Web.Olap.HeaderStyle(); colStyle.Expand = new MSSQLBI.Web.Style( Color.DarkGray, SystemColors.WindowText, Color.Black, Unit.Pixel(1), BorderStyle.None, BorderStyle.None, BorderStyle.Solid, BorderStyle.Solid, MSSQLBI.Web.TextAlign.Left);
