The example shows how to connect to an Analysis server and query a cube using the control.

            private void Page_Load(object sender, System.EventArgs e) 
            {
            	if( !Page.IsPostback )
            	{
            		// connect to the analysis server
            		OlapControl.Connection = new MdxConnection(
            			"http://MY_XMLA_SERVER/xmla/msxisapi.dll"	// url of the XML/A server
            			,"Provider=MSOLAP;Data Source=local"	// data source
            			, "FoodMart 2000"	// catalog to use
            			, "Warehouse"	// cube name
            			);

            		// get the result cube object
            		MSSQLBI.XmlAnalysis.Data.ResultCube rc = OlapControl1.ResultCube;

            		// add Time dimension to column axis
            		rc.ColumnAxis.Add("[Time]");

            		// add Store dimension to row axis
            		rc.RowAxis.Add("[Store]");
            		// initially drill down on All Stores which is the first member of the dimension
            		rc.RowAxis["[Store]"].Members[0].MemberAction = MSSQLBI.XmlAnalysis.Data.MemberState.ShowChildren;

            		// add Units Ordered and Units Shipped measures
            		rc.Measures.Add("[Measures].[Units Ordered]");
            		rc.Measures.Add("[Measures].[Units Shipped]");

            		// add Store Type to the slicer pane
            		rc.SlicerAxis.Add("[Store Type]");
            	}
            }

Following example shows portion of code where static methods provided by the control
           are used to fill in combo boxes with available datasources, catalogs and cubes for a given url.

           private void FillDataSources(string url) 
           {
           	this.cmbDataSource.Items.Clear();
           	MSSQLBI.XmlAnalysis.Data.DataSource[] dsList=MSSQLBI.Web.Olap.OlapControl.GetDataSourceList(url,null);
           	foreach(MSSQLBI.XmlAnalysis.Data.DataSource ds in dsList) 
           	{
           		cmbDataSource.Items.Add(ds);
           	}
           	if(cmbDataSource.Items.Count > 0)
           		cmbDataSource.SelectedIndex=0;
           }
           private void FillCatalogs(MSSQLBI.XmlAnalysis.Data.DataSource ds) 
           {
           	this.cmbCatalog.Items.Clear();
           	MSSQLBI.XmlAnalysis.Data.Catalog[] catalogList=MSSQLBI.Web.Olap.OlapControl.GetCatalogList(ds,null);
           	foreach(MSSQLBI.XmlAnalysis.Data.Catalog catalog in catalogList) 
           	{
           		cmbCatalog.Items.Add(catalog);
           	}
           	if(cmbCatalog.Items.Count > 0) 
           		cmbCatalog.SelectedIndex=0;
           }
           private void FillCubes(MSSQLBI.XmlAnalysis.Data.Catalog catalog) 
           {
           	this.cmbCube.Items.Clear();
           	MSSQLBI.XmlAnalysis.Data.Cube[] cubeList=MSSQLBI.Web.Olap.OlapControl.GetCubeList(catalog,null);
           	foreach(MSSQLBI.XmlAnalysis.Data.Cube cube in cubeList) 
           	{
           		cmbCube.Items.Add(cube);
           	}
           	if(cmbCube.Items.Count > 0) 
           		cmbCube.SelectedIndex=0;
           }
           private void cmbDataSource_SelectedIndexChanged(object sender, System.EventArgs e)
           {
           	if(cmbDataSource.SelectedItem!=null) 
           	{
           		FillCatalogs((MSSQLBI.XmlAnalysis.Data.DataSource)cmbDataSource.SelectedItem);
           	}
           }
           private void cmbCatalog_SelectedIndexChanged(object sender, System.EventArgs e)
           {
           	if(cmbCatalog.SelectedItem!=null) 
           	{
           		FillCubes((MSSQLBI.XmlAnalysis.Data.Catalog)cmbCatalog.SelectedItem);
           	}
           }
           private void btnUrl_Click(object sender,System.EventArgs e) 
           {
           	if(txtUrl.Text != string.Empty) 
           		FillDataSources(this.txtUrl.Text);
           }

