member.code:
/*
* This example creates two distinct header styles, add them to the
* ColumnHeaderStyles collection, then design the result cube such that
* column axis has atleast two dimensions. 
* The first header style is applied to the first dimension on the column axis
* while the second header style is applied to the next.
*/
private void Page_Load(object sender, System.EventArgs e)
{
	if(!Page.IsPostBack) 
	{
		CreateColumnStyles();

		// first column style (gray-style) is applied to store
		OlapControl1.ResultCube.ColumnAxis.Add("[Store]");

		// second column style (khaki-style) is applied to time
		OlapControl1.ResultCube.ColumnAxis.Add("[Time]");

		// rest of the code 			
	}
}

private void CreateColumnStyles() 
{
		MSSQLBI.Web.Olap.HeaderStyle colStyleGray = new MSSQLBI.Web.Olap.HeaderStyle();
		colStyleGray.Expand = new MSSQLBI.Web.Style(
			Color.DarkGray,
			SystemColors.WindowText,
			Color.Black,
			Unit.Pixel(1),
			BorderStyle.None,
			BorderStyle.None,
			BorderStyle.Solid,
			BorderStyle.Solid,
			MSSQLBI.Web.TextAlign.Left);

		colStyleGray.Collapse = new MSSQLBI.Web.Style(
			Color.Gray,
			SystemColors.WindowText,
			Color.Black,
			Unit.Pixel(1),
			BorderStyle.None,
			BorderStyle.None,
			BorderStyle.Solid,
			BorderStyle.Solid,
			MSSQLBI.Web.TextAlign.Left);

		colStyleGray.Aggregate = new MSSQLBI.Web.Style(
			Color.LightGray,
			SystemColors.WindowText,
			Color.Black,
			Unit.Pixel(1),
			BorderStyle.None,
			BorderStyle.None,
			BorderStyle.Solid,
			BorderStyle.Solid,
			MSSQLBI.Web.TextAlign.Left);

		MSSQLBI.Web.Olap.HeaderStyle colStyleKhaki = new MSSQLBI.Web.Olap.HeaderStyle();
		colStyleKhaki.Expand = new MSSQLBI.Web.Style(
			Color.Khaki,
			SystemColors.WindowText,
			Color.Olive,
			Unit.Pixel(1),
			BorderStyle.None,
			BorderStyle.None,
			BorderStyle.Solid,
			BorderStyle.Solid,
			MSSQLBI.Web.TextAlign.Left);

		colStyleKhaki.Collapse = new MSSQLBI.Web.Style(
			Color.DarkKhaki,
			SystemColors.WindowText,
			Color.Olive,
			Unit.Pixel(1),
			BorderStyle.None,
			BorderStyle.None,
			BorderStyle.Solid,
			BorderStyle.Solid,
			MSSQLBI.Web.TextAlign.Left);

		colStyleKhaki.Aggregate = new MSSQLBI.Web.Style(
			Color.PaleGoldenRod,
			SystemColors.WindowText,
			Color.Olive,
			Unit.Pixel(1),
			BorderStyle.None,
			BorderStyle.None,
			BorderStyle.Solid,
			BorderStyle.Solid,
			MSSQLBI.Web.TextAlign.Left);

		OlapControl1.ColumnHeaderStyles.Add(colStyle1);	
		OlapControl1.ColumnHeaderStyles.Add(colStyleKhaki);	
}

/*
* This example creates two distinct header styles, add them to the
* RowHeaderStyles collection, then design the result cube such that
* row axis has atleast two dimensions. 
* The first header style is applied to the first dimension on the row axis
* while the second header style is applied to the next.
*/
private void Page_Load(object sender, System.EventArgs e)
{
	if(!Page.IsPostBack) 
	{
		CreateRowStyles();

		// first row style (gray-style) is applied to store
		OlapControl1.ResultCube.RowAxis.Add("[Store]");

		// second row style (khaki-style) is applied to time
		OlapControl1.ResultCube.RowAxis.Add("[Time]");

		// rest of the code 			
	}
}

private void CreateRowStyles() 
{
		MSSQLBI.Web.Olap.HeaderStyle rowStyleGray = new MSSQLBI.Web.Olap.HeaderStyle();
		rowStyleGray.Expand = new MSSQLBI.Web.Style(
			Color.DarkGray,
			SystemColors.WindowText,
			Color.Black,
			Unit.Pixel(1),
			BorderStyle.None,
			BorderStyle.None,
			BorderStyle.Solid,
			BorderStyle.Solid,
			MSSQLBI.Web.TextAlign.Left);

		rowStyleGray.Collapse = new MSSQLBI.Web.Style(
			Color.Gray,
			SystemColors.WindowText,
			Color.Black,
			Unit.Pixel(1),
			BorderStyle.None,
			BorderStyle.None,
			BorderStyle.Solid,
			BorderStyle.Solid,
			MSSQLBI.Web.TextAlign.Left);

		rowStyleGray.Aggregate = new MSSQLBI.Web.Style(
			Color.LightGray,
			SystemColors.WindowText,
			Color.Black,
			Unit.Pixel(1),
			BorderStyle.None,
			BorderStyle.None,
			BorderStyle.Solid,
			BorderStyle.Solid,
			MSSQLBI.Web.TextAlign.Left);

		MSSQLBI.Web.Olap.HeaderStyle rowStyleKhaki = new MSSQLBI.Web.Olap.HeaderStyle();
		rowStyleKhaki.Expand = new MSSQLBI.Web.Style(
			Color.Khaki,
			SystemColors.WindowText,
			Color.Olive,
			Unit.Pixel(1),
			BorderStyle.None,
			BorderStyle.None,
			BorderStyle.Solid,
			BorderStyle.Solid,
