			BorderStyle.None,
			BorderStyle.None,
			BorderStyle.Solid,
			BorderStyle.Solid,
			MSSQLBI.Web.TextAlign.Left);

		rowStyleKhaki.Aggregate = new MSSQLBI.Web.Style(
			Color.PaleGoldenRod,
			SystemColors.WindowText,
			Color.Olive,
			Unit.Pixel(1),
			BorderStyle.None,
			BorderStyle.None,
			BorderStyle.Solid,
			BorderStyle.Solid,
			MSSQLBI.Web.TextAlign.Left);

		OlapControl1.RowHeaderStyles.Add(colStyle1);	
		OlapControl1.RowHeaderStyles.Add(rowStyleKhaki);	
}

