Shows how to use the locale (language) of the olap cube (if known) to provide single-language
           cube to all users.

           private void Page_Load(object sender, System.EventArgs e) 
           {
           	string locale = this.Session["PreferredLanguage"]; // locale string like "ja"
           	// suppose our catalog naming scheme is CATALOG NAME followed by LANGUAGE PREFIX
           	string catalogName = this.Session["CatalogName"] + locale; 

           	// create connection to the specified language catalog
           	OlapControl1.Connection = new MdxConnection (
           		"http://MY_XMLA_SERVER/xmla/msxisapi.dll",
           		"Provider=MSOLAP;Data Source=local",
           		catalogName,
           		"Warehouse"
           	);

           	OlapControl1.ResultCube.Measures.Add("[Measures].[Units Ordered]");
           	OlapControl1.ResultCube.Measures.Add("[Measures].[Units Shipped]");
           	OlapControl1.ResultCube.ColumnAxis.Add("[Store]");
           	OlapControl1.ResultCube.ColumnAxis.Add("[Measures]");
           	OlapControl1.ResultCube.RowAxis.Add("[Time]");

           	// create a culture info from the preferred language prefix
           	System.Globalization.CultureInfo ci = new System.Globalization.CultureInfo(locale);

           	// read the measure caption from the resource for the specified culture 
           	OlapControl1.MeasuresCaption = resourceManager.GetString("MEASURE_CAPTION",ci);

           	// read the include measure expression for store dimension for the specified culture
           	OlapControl1.ResultCube.ColumnAxis["[Store]"].IncludeMeasureExpression = resourceManager.GetString("STORE_TOTAL_EXPR", ci);

           	// read the exclude measure expression for time dimension for the specified culture
           	OlapControl1.ResultCube.RowAxis["[Time]"].ExcludeMeasureExpression = resourceManager.GetString("TIME_TOTAL_EXPR", ci);
           }

