Here is an example showing how to do drillthrough using the control:

            public class drillthroughsample : System.Web.UI.Page
            {
            	// assume that we have an instance of OlapControl on the page 
            	// with connection been established

            	private void InitializeComponent()
            	{
            		// this is auto-generated when you add an event handler for OnDrillthrough event
            		this.OlapControl1.Drillthrough += new MSSQLBI.Web.Olap.DrilledthroughEventHandler(this.OlapControl1_Drillthrough);

            		// other initialization code might be here
            	}

            	private void OlapControl1_Drillthrough(object source, MSSQLBI.Web.Olap.DrilledthroughEventArgs e)
            	{
            		MSSQLBI.XmlAnalysis.Data.DrillthruCell dc = e.Drillthrough;

            		// store the connection and the drillthru cell object in session state
            		// so that we can retrieve them from a different page
            		this.Session["MdxConnection"] = OlapControl1.Connection;
            		this.Session["DrillthruCell"] = dc;

            		// create an onload script block which requests for the drillthru result page
            		this.RegisterStartupScript("pageload", "<script language=JavaScript>window.open('drillthrough.aspx', 'Drillthrough', 'left=50,top=50,height=450,width=600,status=yes,toolbar=no,menubar=no,location=no,scrollbars=yes,resizable=yes', true); </script>");
            	}
            }

            /////////////////////		drillthru.aspx		////////////////////////

            public class drillthrough : System.Web.UI.Page
            {
            	protected System.Web.UI.WebControls.DataGrid gridDrillThrough;

            	private void Page_Load(object sender, System.EventArgs e)
            	{
            		if(!Page.IsPostBack)
            		{
            			gridDrillThrough.DataSource = GetDrillThrough();
            			gridDrillThrough.DataBind();
            		}
            	}

            	private DataSet GetDrillThrough()
            	{
            		// Put user code to initialize the page here
            		MSSQLBI.XmlAnalysis.Data.DrillthruCell dc = (MSSQLBI.XmlAnalysis.Data.DrillthruCell)(this.Session["DrillthruCell"]);
            		MSSQLBI.XmlAnalysis.Data.MdxConnection connection = (MSSQLBI.XmlAnalysis.Data.MdxConnection)this.Session["MdxConnection"];

            		// bind to datagrid
            		dc.MaxRows = 200;
            		System.Xml.XmlElement xe = MSSQLBI.Web.Olap.OlapControl.ExecuteQuery(connection, dc.GetMdx()[0], false);

            		DataSet ds = new DataSet();
            		System.IO.StringReader sr = new System.IO.StringReader(xe.OuterXml);
            		ds.ReadXml(sr, System.Data.XmlReadMode.Auto);

            		return ds;
            	}
            }

